const baseURL = 'http://localhost:5000/api/cards/';

const GameService = {

    getCards(){
        return fetch(baseURL)
        .then(res => res.json())
    },

    getOneCard(id){
        return fetch(baseURL+id)
        .then(res => res.json())
    }

};

export default GameService;
